import React, { useState } from 'react';

const caseStudies = [
  {
    logo: 'LOGO',
    title: 'Porem ipsum',
    subtitle: 'Vorem ipsum dolor sit amet, consectetur adipiscing elit.',
    content:
      'Rorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis.',
  },
  {
    logo: 'LOGO',
    title: 'Korem ipsum',
    subtitle: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    cta: 'Case Study',
    content:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Ut commodo efficitur neque. Ut diam quam, semper iaculis condimentum ac, vestibulum eu nisl.',
  },
  {
    logo: 'LOGO',
    title: 'Worem ipsum',
    subtitle: 'Vorem ipsum dolor sit amet, consectetur adipiscing elit.',
    content:
      'Forem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Ut commodo efficitur neque.',
  },
];

const CaseStudySection = () => {
  const [index, setIndex] = useState(1); // Center card by default
  const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;

  const handlePrev = () => setIndex((i) => Math.max(i - 1, 0));
  const handleNext = () => setIndex((i) => Math.min(i + 1, caseStudies.length - 1));

  // Responsive: show 1 card on mobile, 3 on desktop
  let visibleCards, startIdx;
  if (typeof window !== 'undefined' && window.innerWidth < 768) {
    visibleCards = 1;
    startIdx = index;
  } else {
    visibleCards = 3;
    startIdx = Math.max(0, index - 1);
  }

  return (
    <section className="py-5 bg-white">
      <div className="container">
        <h1 className="text-center fw-bold mb-5" style={{ fontSize: '3rem', lineHeight: 1.1 }}>
          Horem ipsum dolor sit amet,<br />
          <span style={{ color: '#3ec28f', fontWeight: 600 }}>consectetur<br />adipiscing elit.</span>
        </h1>
        <div className="d-flex align-items-center justify-content-center position-relative" style={{ minHeight: 420 }}>
          <button
            onClick={handlePrev}
            className="btn btn-link p-0 me-3 d-flex align-items-center justify-content-center"
            style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none' }}
            disabled={index === 0}
            aria-label="Previous"
          >
            <span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8592;</span>
          </button>
          <div className="d-flex flex-row justify-content-center w-100 gap-4 case-study-carousel-track" style={{ maxWidth: 1400 }}>
            {caseStudies.slice(startIdx, startIdx + visibleCards).map((card, i) => (
              <div
                key={i}
                className="bg-white rounded-4 shadow-sm px-4 py-4"
                style={{
                  border: '2px solid #e6e8ec',
                  minWidth: 320,
                  maxWidth: 420,
                  flex: 1,
                  margin: '0 8px',
                  minHeight: 380,
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'flex-start',
                }}
              >
                <div className="mb-2" style={{ color: '#a6ff4d', fontWeight: 600, fontSize: '2.2rem', letterSpacing: 1 }}>
                  {card.logo}
                </div>
                <div className="mb-2">
                  <span className="fw-bold" style={{ fontSize: '1.5rem', color: '#222' }}>{card.title}</span>
                </div>
                <div className="mb-3" style={{ color: '#222', fontSize: '1rem', fontWeight: 500, letterSpacing: 0.2 }}>{card.subtitle}</div>
                {card.cta && (
                  <div className="mb-2 d-flex align-items-center" style={{ fontWeight: 600, fontSize: '1.1rem', color: '#222' }}>
                    {card.cta}
                    <span style={{ color: '#3ec28f', fontSize: 28, marginLeft: 8, fontWeight: 600 }}>&#8594;</span>
                  </div>
                )}
                <div style={{ color: '#222', fontSize: '1.08rem', fontWeight: 400, marginTop: 8 }}>{card.content}</div>
              </div>
            ))}
          </div>
          <button
            onClick={handleNext}
            className="btn btn-link p-0 ms-3 d-flex align-items-center justify-content-center"
            style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none' }}
            disabled={index === caseStudies.length - 1}
            aria-label="Next"
          >
            <span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8594;</span>
          </button>
        </div>
      </div>
      <style>{`
        @media (max-width: 991px) {
          .case-study-carousel-track > div { min-width: 90vw !important; max-width: 98vw; }
        }
        @media (max-width: 767px) {
          .case-study-carousel-track { gap: 0 !important; }
          .case-study-carousel-track > div { min-width: 98vw !important; max-width: 99vw; margin: 0 !important; }
        }
      `}</style>
    </section>
  );
};

export default CaseStudySection;
