import React, { useEffect, useState } from 'react';

const BLOG_API = 'https://futureoffounders.com/wp-json/wp/v2/posts';

function Blog() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(BLOG_API)
      .then(res => res.json())
      .then(data => {
        setPosts(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load blog posts.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="container-fluid px-0" style={{ background: '#f7f8fa', minHeight: '100vh' }}>
      {/* Header */}
      <nav className="navbar navbar-expand-lg navbar-light bg-white py-3 border-bottom">
        <div className="container">
          <a className="navbar-brand" href="/">
            <img src="/logo192.png" alt="Logo" width="40" height="40" />
          </a>
          {/* ...existing nav code... */}
        </div>
      </nav>
      <div className="container py-4">
        <div className="text-center mb-4">
          <h2 className="fw-bold mb-2" style={{ fontSize: '2rem', color: '#222' }}>Gorem ipsum dolor sit amet</h2>
          <div className="mb-2" style={{ borderBottom: '4px solid #3ec28f', width: '120px', margin: '0 auto' }}></div>
          <h1 className="fw-bold" style={{ color: '#3ec28f', fontSize: '2.5rem' }}>Blog</h1>
        </div>
        {/* Search and Sort */}
        <div className="row mb-4 align-items-center">
          <div className="col-md-10 mb-2 mb-md-0">
            <input className="form-control" placeholder="Search..." style={{ borderRadius: '2rem', paddingLeft: '2.5rem' }} />
          </div>
          <div className="col-md-2 text-md-end">
            <button className="btn btn-outline-secondary w-100" style={{ borderRadius: '2rem' }}>Sort By <span className="ms-1">&#x25BC;</span></button>
          </div>
        </div>
        {/* Featured Press Releases */}
        <h5 className="fw-bold mb-3">Featured Press Releases</h5>
        <div className="row mb-5">
          {loading ? (
            <div className="text-center py-5">Loading...</div>
          ) : error ? (
            <div className="text-danger text-center py-5">{error}</div>
          ) : (
            posts.slice(0, 4).map(post => (
              <div className="col-md-3 mb-4" key={post.id}>
                <BlogCard post={post} />
              </div>
            ))
          )}
        </div>
        {/* All Press Releases */}
        <h5 className="fw-bold mb-3">All Press Releases</h5>
        <div className="row">
          {loading ? (
            <div className="text-center py-5">Loading...</div>
          ) : error ? (
            <div className="text-danger text-center py-5">{error}</div>
          ) : (
            posts.map(post => (
              <div className="col-md-3 mb-4" key={post.id}>
                <BlogCard post={post} />
              </div>
            ))
          )}
        </div>
        {/* Pagination (static for now) */}
        <nav className="d-flex justify-content-center mt-4">
          <ul className="pagination">
            <li className="page-item"><a className="page-link" href="#">&lt;</a></li>
            <li className="page-item active"><a className="page-link" href="#">1</a></li>
            <li className="page-item"><a className="page-link" href="#">2</a></li>
            <li className="page-item"><a className="page-link" href="#">3</a></li>
            <li className="page-item"><a className="page-link" href="#">4</a></li>
            <li className="page-item"><a className="page-link" href="#">&gt;</a></li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

function BlogCard({ post }) {
  // Use post.featured_media for image if available, else fallback
  const img = 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80';
  return (
    <div className="card shadow-sm px-3 py-3" style={{ borderRadius: '22px', border: '1.5px solid #e5e5e5', minHeight: '370px' }}>
      <img src={img} alt={post.title.rendered} style={{ width: '100%', borderRadius: '16px', marginBottom: '1.2rem', objectFit: 'cover', height: '150px' }} />
      <div className="fw-bold mb-1" style={{ fontSize: '1.1rem', color: '#222' }}>{post.title.rendered}</div>
      <div className="mb-3" style={{ color: '#222', fontSize: '1.08rem', lineHeight: 1.35 }}>
        {post.excerpt.rendered.replace(/<[^>]+>/g, '').slice(0, 120)}
      </div>
      <div className="fw-bold d-flex align-items-center" style={{ fontSize: '1.08rem', color: '#222' }}>
        Read Case Study
        <span className="ms-2" style={{ color: '#07c167', fontSize: '1.5rem', fontWeight: 600 }}>&#8594;</span>
      </div>
    </div>
  );
}

export default Blog;
