import React, { useState } from 'react';

const caseStudies = [
	{
		logo: 'LOGO',
		title: 'Porem ipsum',
		subtitle: 'Vorem ipsum dolor sit amet, consectetur adipiscing elit.',
		content:
			'Rorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis.',
	},
	{
		logo: 'LOGO',
		title: 'Korem ipsum',
		subtitle: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
		cta: 'Case Study',
		content:
			'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Ut commodo efficitur neque. Ut diam quam, semper iaculis condimentum ac, vestibulum eu nisl.',
	},
	{
		logo: 'LOGO',
		title: 'Worem ipsum',
		subtitle: 'Vorem ipsum dolor sit amet, consectetur adipiscing elit.',
		content:
			'Forem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Ut commodo efficitur neque.',
	},
	{
		logo: 'LOGO',
		title: 'Dorem ipsum',
		subtitle: 'Dorem ipsum dolor sit amet, consectetur adipiscing elit.',
		content:
			'Dorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Etiam facilisis, sapien eu cursus dictum, enim urna facilisis sem, nec cursus enim urna euismod sem.',
	},
	{
		logo: 'LOGO',
		title: 'Morem ipsum',
		subtitle: 'Morem ipsum dolor sit amet, consectetur adipiscing elit.',
		content:
			'Morem ipsum dolor sit amet, consectetur adipiscing elit. Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Curabitur tempus urna at turpis condimentum lobortis. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas.',
	},
];

const CaseStudySection = () => {
	const [index, setIndex] = useState(0);
	const [isMobile, setIsMobile] = useState(false);

	React.useEffect(() => {
		const checkMobile = () => setIsMobile(window.innerWidth < 768);
		checkMobile();
		window.addEventListener('resize', checkMobile);
		return () => window.removeEventListener('resize', checkMobile);
	}, []);

	const visibleCards = isMobile ? 1 : 3;
	const maxIndex = caseStudies.length - visibleCards;

	const handlePrev = () => setIndex((i) => Math.max(i - 1, 0));
	const handleNext = () => setIndex((i) => Math.min(i + 1, maxIndex));

	return (
		<section className="py-5 bg-white">
			<div className="container">
				<h1 className="text-center fw-bold mb-5" style={{ fontSize: '3rem', lineHeight: 1.1 }}>
					Horem ipsum dolor sit amet,<br />
					<span style={{ color: '#3ec28f', fontWeight: 600 }}>consectetur<br />adipiscing elit.</span>
				</h1>
				<div className="d-flex align-items-center justify-content-center position-relative" style={{ minHeight: 420 }}>
					{isMobile && (
						<button
							onClick={handlePrev}
							className="btn btn-link p-0 d-flex align-items-center justify-content-center case-arrow-mobile case-arrow-mobile-left"
							style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none', position: 'absolute', left: 0, top: '50%', transform: 'translateY(-50%)', zIndex: 2 }}
							disabled={index === 0}
							aria-label="Previous"
						>
							<span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8592;</span>
						</button>
					)}
					{!isMobile && (
						<button
							onClick={handlePrev}
							className="btn btn-link p-0 me-3 d-flex align-items-center justify-content-center"
							style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none' }}
							disabled={index === 0}
							aria-label="Previous"
						>
							<span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8592;</span>
						</button>
					)}
					<div className="d-flex flex-row justify-content-center w-100 gap-4 case-study-carousel-track" style={{ maxWidth: 1400 }}>
						{caseStudies.slice(index, index + visibleCards).map((card, i) => (
							<div
								key={i}
								className="bg-white rounded-4 shadow-sm px-4 py-4"
								style={{
									border: '2px solid #e6e8ec',
									minWidth: isMobile ? '96vw' : 380,
									maxWidth: isMobile ? '96vw' : 440,
									flex: 1,
									margin: isMobile ? '0 auto' : '0 8px',
									minHeight: 380,
									display: 'flex',
									flexDirection: 'column',
									justifyContent: 'flex-start',
									borderRadius: '20px',
									boxShadow: isMobile ? '0 2px 16px #0001' : undefined,
									padding: isMobile ? '2.2rem 1.2rem' : undefined,
								}}
							>
								<div className="mb-2" style={{ color: '#a6ff4d', fontWeight: 600, fontSize: '2.2rem', letterSpacing: 1 }}>
									{card.logo}
								</div>
								<div className="mb-2">
									<span className="fw-bold" style={{ fontSize: '1.5rem', color: '#222' }}>{card.title}</span>
								</div>
								<div className="mb-3" style={{ color: '#222', fontSize: '1rem', fontWeight: 500, letterSpacing: 0.2 }}>{card.subtitle}</div>
								{card.cta && (
									<div className="mb-2 d-flex align-items-center" style={{ fontWeight: 600, fontSize: '1.1rem', color: '#222' }}>
										{card.cta}
										<span style={{ color: '#3ec28f', fontSize: 28, marginLeft: 8, fontWeight: 600 }}>&#8594;</span>
									</div>
								)}
								<div style={{ color: '#222', fontSize: '1.08rem', fontWeight: 400, marginTop: 8 }}>{card.content}</div>
							</div>
						))}
					</div>
					{isMobile && (
						<button
							onClick={handleNext}
							className="btn btn-link p-0 d-flex align-items-center justify-content-center case-arrow-mobile case-arrow-mobile-right"
							style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none', position: 'absolute', right: 0, top: '50%', transform: 'translateY(-50%)', zIndex: 2 }}
							disabled={index === maxIndex}
							aria-label="Next"
						>
							<span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8594;</span>
						</button>
					)}
					{!isMobile && (
						<button
							onClick={handleNext}
							className="btn btn-link p-0 ms-3 d-flex align-items-center justify-content-center"
							style={{ color: '#3ec28f', fontSize: 36, border: 'none', background: 'none', outline: 'none', boxShadow: 'none' }}
							disabled={index === maxIndex}
							aria-label="Next"
						>
							<span style={{ fontSize: 48, fontWeight: 400, lineHeight: 1 }}>&#8594;</span>
						</button>
					)}
				</div>
			</div>
			<style>{`
				@media (max-width: 991px) {
					.case-study-carousel-track > div { min-width: 90vw !important; max-width: 98vw; }
				}
				@media (max-width: 767px) {
					.case-study-carousel-track {
						gap: 0 !important;
						flex-wrap: nowrap !important;
						overflow-x: hidden !important;
						justify-content: center !important;
						align-items: center !important;
						width: 100vw !important;
						margin-left: 0 !important;
						margin-right: 0 !important;
					}
					.case-study-carousel-track > div {
						min-width: 92vw !important;
						max-width: 92vw !important;
						margin: 0 auto !important;
						float: none !important;
						border-radius: 24px !important;
						padding: 2.2rem 2.2rem 2.2rem 2.2rem !important;
						box-shadow: 0 2px 16px #0001 !important;
					}
					.case-arrow-mobile {
						position: absolute !important;
						top: 50% !important;
						transform: translateY(-50%) !important;
						z-index: 10 !important;
						background: none !important;
						border: none !important;
						box-shadow: none !important;
						padding: 0 !important;
						font-size: 2.5rem !important;
					}
					.case-arrow-mobile-left { left: -40px !important; }
					.case-arrow-mobile-right { right: -40px !important; }
				}
			`}</style>
		</section>
	);
// ...existing code...
};

export default CaseStudySection;
